graphql.query:
query GetUserRatings {
  userRatings(userId: 1) {
    id
    ratedUserId
    ratedByUserId
    ratingValue
    review
    ratingType
    createdAt
    updatedAt
  }
}

query GetUserFollowers {
  userFollowers(userId: 1) {
    id
    userId
    followingId
    status
    followedAt
  }
}

query GetUserFollowing {
  userFollowing(userId: 1) {
    id
    userId
    followingId
    status
    followedAt
  }
}

mutation FollowUser {
  followUser(
    userId: 1
    followingId: 2
  ) {
    id
    userId
    followingId
    status
    followedAt
  }
}

query CheckFollowingStatus {
  checkFollowingStatus(userId: 1, followingId: 2) {
    id
    userId
    followingId
    status
    followedAt
  }
}

mutation SendVerificationOTP {
  sendOtp(
    email: "ram9151@gmail.com"
    type: VERIFICATION
  ) {
    success
    message
    channels  # ["email"] or ["email", "sms"] if phone provided
  }
}

mutation SendLoginOTP {
  sendOtp(
    email: "ram9151@gmail.com"
    type: LOGIN
  ) {
    success
    message
    channels
  }
}

mutation VerifyPasswordResetOTP {
  verifyOtp(
    email: "ram9151@gmail.com"
    otpCode: "139397"
    type: PASSWORD_RESET
  ) {
    success
    message
    userInfo {
      email
      emailVerified
    }
  }
}

mutation ResetPassword {
  resetPassword(
    email: "ram9151@gmail.com"
    otpCode: "139397"
    newPassword: "1234"
    confirmPassword: "1234"
  ) {
    success
    message
    userInfo {
      email
      emailVerified
    }
  }
}

mutation CreatePost {
  createPost(
    userId: 1
    title: "Beautiful House for Sale"
    content: "3 bedroom house with garden"
    visibility: "public"
    propertyType: "house"
    location: "New York"
    mapLocation: "40.7128,-74.0060"
    price: 500000.0
    status: "active"
    media: [{
      mediaType: "image"
      mediaData: "SGVsbG8s="  # This is "Hello" in base64
      mediaOrder: 1
      caption: "Front view"
    }]
  ) {
    success
    message
    post {
      id
      title
      media {
        id
        mediaUrl
        caption
      }
    }
  }
}

mutation UpdatePost {
  updatePost(
    postId: 2
    title: "chikkadpally"
    price: 550000.0
    status: "pending"
  ) {
    success
    message
    post {
      id
      title
      price
      status
    }
  }
}

mutation DeletePost {
  deletePost(postId: 2) {
    success
    message
  }
}

mutation AddPostMedia {
  addPostMedia(
    postId: 4    
    media: [{
      mediaType: "image"
      mediaData: "SGVsbG8s="
      mediaOrder: 2
      caption: "Garden view"
    }]
  ) {
    success
    message
    post {
      id
      media {
        id
        mediaUrl
        caption
      }
    }
  }
}

mutation DeletePostMedia {
  deletePostMedia(mediaId: 3) {
    success
    message
  }
}

mutation LikePost {
  likePost(postId: 4, userId: 3) {
    success
    message
    post {
      id
      likeCount
    }
  }
}

mutation UnlikePost {
  unlikePost(postId: 4, userId: 3) {
    success
    message
    post {
      id
      likeCount
    }
  }
}

mutation CreateComment {
  createComment(
    postId: 4
    userId: 5,
    parentCommentId: 14
    comment: "Great property!"
  ) {
    success
    message
    comment {
      id
      comment
      addedAt
      likeCount
    }
  }
}

mutation CreateCommentReply {
  createComment(
    postId: 4
    userId: 2
    comment: "Thanks for your feedback!"
    parentCommentId: 15
  ) {
    success
    message
    comment {
      id
      parentCommentId
      comment
      addedAt
    }
  }
}

mutation UpdateComment {
  updateComment(
    commentId: 4
    comment: "Updated comment text"
  ) {
    success
